        {{ elementary.get_unified_metrics_query(metrics=table_monitors,
                                                metric_properties=metric_properties) }}
    ),

    metrics_final as (
        select
            {{ elementary.edr_cast_as_string(full_table_name_str) }} as full_table_name,
