        {% if metadata_hashes is not none %}
            {% do return({"type": "diff", "metadata_hashes": metadata_hashes}) %}
        {% else %}
            {% do return({"type": "replace"}) %}
        {% endif %}
    {% elif upload_artifacts_method == "replace" %}
        {% do return({"type": "replace"}) %}
    {% else %}
